
using System.IO;
using System.Text;

namespace KPIT_K_Foundation
{
  internal class GlobalCss
  {
    private string _fullFileNamePath;
    private string _jqueryTheme;
    private bool _isUseJqueryValidation;
    private string _appVersion;
    private ApplicationType _appType;

    internal GlobalCss()
    {
    }

    internal GlobalCss(string fullFileNamePath, string jqueryTheme, bool isUseJqueryValidation, string appVersion, ApplicationType appType = ApplicationType.ASPNET)
    {
      this._fullFileNamePath = fullFileNamePath;
      this._jqueryTheme = jqueryTheme;
      this._isUseJqueryValidation = isUseJqueryValidation;
      this._appVersion = appVersion;
      this._appType = appType;
      if (this._appType == ApplicationType.ASPNET45)
        this.GenerateCss45();
      else
        this.GenerateCss();
    }

    private void GenerateCss()
    {
      using (StreamWriter streamWriter = new StreamWriter(this._fullFileNamePath))
      {
        StringBuilder stringBuilder = new StringBuilder();
        if (this._appVersion.ToLower() != "express")
        {
          stringBuilder.AppendLine("/**************************** jquery ui *******************************/");
          stringBuilder.AppendLine("@import \"../JQueryThemes/" + this._jqueryTheme + "/jquery-ui-1.8.11.custom.css\";");
          stringBuilder.AppendLine("");
          if (this._isUseJqueryValidation)
          {
            stringBuilder.AppendLine("/**************************** jquery validate *************************/");
            stringBuilder.AppendLine("@import \"core.css\";");
            stringBuilder.AppendLine("@import \"cmxformTemplate.css\";");
            stringBuilder.AppendLine("@import \"cmxform.css\";");
            stringBuilder.AppendLine("");
            stringBuilder.AppendLine("form.MasterPageForm1 { width: 50em; }");
            stringBuilder.AppendLine("");
            stringBuilder.AppendLine("em.error {");
            stringBuilder.AppendLine("    background: url(\"../Images/Unchecked.gif\") no-repeat 0px 0px;");
            stringBuilder.AppendLine("    padding-left: 16px;");
            stringBuilder.AppendLine("}");
            stringBuilder.AppendLine("");
            stringBuilder.AppendLine("em.success {");
            stringBuilder.AppendLine("    background: url(\"../Images/Checked.gif\") no-repeat 0px 0px;");
            stringBuilder.AppendLine("    padding-left: 16px;");
            stringBuilder.AppendLine("}");
            stringBuilder.AppendLine("");
            stringBuilder.AppendLine("form.MasterPageForm1 label.error {");
            stringBuilder.AppendLine("    margin-left: auto;");
            stringBuilder.AppendLine("}");
            stringBuilder.AppendLine("");
            stringBuilder.AppendLine("em.error { color: black; }");
            stringBuilder.AppendLine("#warning { display: none; }");
            stringBuilder.AppendLine("");
          }
        }
        stringBuilder.AppendLine("/****************************** body ********************************/");
        stringBuilder.AppendLine("body, textarea {");
        stringBuilder.AppendLine("   font-family: 'lucida grande', helvetica, verdana, arial, sans-serif;");
        stringBuilder.AppendLine("   font-size: 12px;");
        stringBuilder.AppendLine("   color: #000000;");
        stringBuilder.AppendLine("}");
        stringBuilder.AppendLine("");
        stringBuilder.AppendLine("a:link, a:visited {");
        stringBuilder.AppendLine("   text-decoration: underline;");
        stringBuilder.AppendLine("}");
        stringBuilder.AppendLine("");
        stringBuilder.AppendLine("a:hover {");
        stringBuilder.AppendLine("   text-decoration: none;");
        stringBuilder.AppendLine("   border-bottom-width: 1px;");
        stringBuilder.AppendLine("}");
        if (this._appVersion.ToLower() != "express")
        {
          stringBuilder.AppendLine("");
          stringBuilder.AppendLine("/****************************** gridview ****************************/");
          stringBuilder.AppendLine(".gridviewPagerStyle a {");
          stringBuilder.AppendLine("    font-size: smaller;");
          stringBuilder.AppendLine("    color: #ffffff;");
          stringBuilder.AppendLine("    text-decoration: none;");
          stringBuilder.AppendLine("    padding: 2px 10px 2px 10px;");
          stringBuilder.AppendLine("    border-top: solid 1px #777777;");
          stringBuilder.AppendLine("    border-right: solid 1px #333333;");
          stringBuilder.AppendLine("    border-bottom: solid 1px #333333;");
          stringBuilder.AppendLine("    border-left: solid 1px #777777;");
          stringBuilder.AppendLine("    background-color: White;");
          stringBuilder.AppendLine("}");
          stringBuilder.AppendLine("");
          stringBuilder.AppendLine(".gridviewPagerStyle span {");
          stringBuilder.AppendLine("    font-size: smaller;");
          stringBuilder.AppendLine("    font-weight: bold;");
          stringBuilder.AppendLine("    color: #000000;");
          stringBuilder.AppendLine("    text-decoration: none;");
          stringBuilder.AppendLine("    padding: 2px 10px 2px 10px;");
          stringBuilder.AppendLine("    border-top: solid 1px #777777;");
          stringBuilder.AppendLine("    border-right: solid 1px #333333;");
          stringBuilder.AppendLine("    border-bottom: solid 1px #333333;");
          stringBuilder.AppendLine("    border-left: solid 1px #777777;");
          stringBuilder.AppendLine("    background-color: #EFF3FB;");
          stringBuilder.AppendLine("}");
          stringBuilder.AppendLine("");
          stringBuilder.AppendLine(".gridviewGridLines td {");
          stringBuilder.AppendLine("    border: 1px solid #E5E5E5;");
          stringBuilder.AppendLine("}");
          stringBuilder.AppendLine("");
          stringBuilder.AppendLine(".gridviewHighlight {");
          stringBuilder.AppendLine("    background-color: Black;");
          stringBuilder.AppendLine("}");
        }
        streamWriter.Write(stringBuilder.ToString());
      }
    }

