        stringBuilder.AppendLine("        color: #000 !important; /* Black prints faster: h5bp.com/s */                   ");
        stringBuilder.AppendLine("        box-shadow: none !important;                                                    ");
        stringBuilder.AppendLine("        text-shadow: none !important;                                                   ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    a,                                                                                  ");
        stringBuilder.AppendLine("    a:visited {                                                                         ");
        stringBuilder.AppendLine("        text-decoration: underline;                                                     ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    a[href]:after {                                                                     ");
        stringBuilder.AppendLine("        content: \" (\" attr(href) \")\";                                               ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    abbr[title]:after {                                                                 ");
        stringBuilder.AppendLine("        content: \" (\" attr(title) \")\";                                              ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    /*                                                                                  ");
        stringBuilder.AppendLine("     * Don't show links for images, or javascript/internal links                        ");
        stringBuilder.AppendLine("     */                                                                                 ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    .ir a:after,                                                                        ");
        stringBuilder.AppendLine("    a[href^=\"javascript:\"]:after,                                                     ");
        stringBuilder.AppendLine("    a[href^=\"#\"]:after {                                                              ");
        stringBuilder.AppendLine("        content: \"\";                                                                  ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    pre,                                                                                ");
        stringBuilder.AppendLine("    blockquote {                                                                        ");
        stringBuilder.AppendLine("        border: 1px solid #999;                                                         ");
        stringBuilder.AppendLine("        page-break-inside: avoid;                                                       ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    thead {                                                                             ");
        stringBuilder.AppendLine("        display: table-header-group; /* h5bp.com/t */                                   ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    tr,                                                                                 ");
        stringBuilder.AppendLine("    img {                                                                               ");
        stringBuilder.AppendLine("        page-break-inside: avoid;                                                       ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    img {                                                                               ");
        stringBuilder.AppendLine("        max-width: 100% !important;                                                     ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    @page {                                                                             ");
        stringBuilder.AppendLine("        margin: 0.5cm;                                                                  ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    p,                                                                                  ");
        stringBuilder.AppendLine("    h2,                                                                                 ");
        stringBuilder.AppendLine("    h3 {                                                                                ");
        stringBuilder.AppendLine("        orphans: 3;                                                                     ");
        stringBuilder.AppendLine("        widows: 3;                                                                      ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("                                                                                        ");
        stringBuilder.AppendLine("    h2,                                                                                 ");
        stringBuilder.AppendLine("    h3 {                                                                                ");
        stringBuilder.AppendLine("        page-break-after: avoid;                                                        ");
        stringBuilder.AppendLine("    }                                                                                   ");
        stringBuilder.AppendLine("}                                                                                       ");
        streamWriter.Write(stringBuilder.ToString());
      }
    }
  }
